import React, {useState} from "react";
import Scrollbar from "react-scrollbars-custom";
import {
  Container,
  Row,
  Col,
  TabContent,
  TabPane,
} from "reactstrap";

import OptionCard from "./components/OptionCard";
import DisplayCard from "./components/DisplayCard";
import DisplayBar from "./components/DisplayBar";
import OptionBar from "./components/OptionBar";
import TopBar from "./components/TopBar";
import setsData from "./data/setsData";
import tagsData from "./data/tagsData";
import typesData from "./data/typesData";

const App = () => {
    const [activeTab, setActiveTab] = useState("Sets");
    const [activeOption, setActiveOption] = useState(setsData[0]);

    function handleOptionClick(content) {
      setActiveOption(content);
    }
    return (
      <>
        <TopBar />
        <Container
          style={{ background: "white", padding: "0", marginTop: "1em" }}
          className="rounded"
        >
          <Row>
            <Col xs="4">
              <OptionBar activeTab={activeTab} setActiveTab={setActiveTab} />
              <TabContent activeTab={activeTab}>
                <TabPane tabId="Sets">
                  <Scrollbar
                    style={{
                      height: "45rem"
                    }}
                  >
                    {setsData.map(item => (
                      <OptionCard
                        key={item.id}
                        content={item}
                        clickHandler={handleOptionClick}
                        activeOption={activeOption}
                        activeTab={activeTab}
                        type="Sets"
                      />
                    ))}
                  </Scrollbar>
                </TabPane>
                <TabPane tabId="Types">
                  <Scrollbar
                    style={{
                      height: "45rem"
                    }}
                  >
                    {typesData.map(item => (
                      <OptionCard
                        key={item.id}
                        content={item}
                        clickHandler={handleOptionClick}
                        activeOption={activeOption}
                        activeTab={activeTab}
                        type="Types"
                      />
                    ))}
                  </Scrollbar>
                </TabPane>
                <TabPane tabId="Tags">
                  <Scrollbar
                    style={{
                      height: "45rem"
                    }}
                  >
                    {tagsData.map(item => (
                      <OptionCard
                        key={item.id}
                        content={item}
                        clickHandler={handleOptionClick}
                        activeOption={activeOption}
                        activeTab={activeTab}
                        type="Tags"
                      />
                    ))}
                  </Scrollbar>
                </TabPane>
              </TabContent>
            </Col>
            <Col xs="8">
              <DisplayBar activeOption={activeOption}/>
              <Scrollbar
                style={{
                  height: "50rem"
                }}
              >
                {activeOption.faqs.map(item => (
                  <DisplayCard content={item} key={item.id} />
                ))}
              </Scrollbar>
            </Col>
          </Row>
        </Container>
      </>
    );
  }


  export default App;
