import React from "react";
import { Badge, Button } from "reactstrap";
import { getFaqsByTag, getFaqsByType } from "../helpers/faqsHelper";

import "../styles.css";
const determineSelected = (active, current) => {
  let isSelected = false;
  if (active.name.toLowerCase() === current.name) isSelected = true;
  if (active.name.toLowerCase().replace(/ /g, "-") === current.name.toLowerCase().replace(/ /g, "-")) isSelected = true;
  return isSelected;
}
export default function OptionCard({
  content,
  clickHandler,
  activeOption,
  activeTab,
}) {
  return (
    <Button
      className="element p-1 m-1 border border-3-dark"
      onClick={() => {
        activeTab === "Sets" && clickHandler(content)
        activeTab === "Types" && clickHandler(getFaqsByType(content.name))
        activeTab === "Tags" && clickHandler(getFaqsByTag(content.id)) }}
      style={{
        background: determineSelected(activeOption, content) ? "#343a40" : "white",
        color: activeOption.name.toLowerCase() === content.name.toLowerCase() ? "white" : "black"
      }}
    >
      <Badge
        color={activeOption.name === content.name ? "light" : "dark"}
        className="element m-1"
      >
        {content.faqs && content.faqs.length}
        {content.total && content.total}
      </Badge>
      {content.name}
    </Button>
  );
}
