import React from "react";
import { Badge, Button } from "reactstrap";
import { getFaqsByTag, getFaqsByType } from "../helpers/faqsHelper";

import "../styles.css";
const removeExtra = (item) => item.toLowerCase().replace(/ /g, "-").replace(/./g, "-")
export default function OptionCard({
  content,
  clickHandler,
  activeOption,
  activeTab,
}) {
  return (
    <Button
      className="element p-1 m-1 border border-3-dark"
      onClick={() => {
        activeTab === "Sets" && clickHandler(content)
        activeTab === "Types" && clickHandler(getFaqsByType(content.name))
        activeTab === "Tags" && clickHandler(getFaqsByTag(content.id)) }}
      style={{
        background: removeExtra(activeOption.name) === removeExtra(content.name) ? "#343a40" : "white",
        color:  determineSelected(activeOption, content) ? "white" : "black"
      }}
    >
      <Badge
        color={determineSelected(activeOption, content) ? "light" : "dark"}
        className="element m-1"
      >
        {content.faqs && content.faqs.length}
        {content.total && content.total}
      </Badge>
      {content.name}
    </Button>
  );
}
