import React from "react";
import { Card, CardBody, CardTitle, CardText, Badge } from "reactstrap";
import faqsHelper from "../helpers/faqsHelper";
export default function OptionCard({
  content,
  clickHandler,
  activeOption,
  activeTab,
  type
}) {
  return (
    <div
      className="element"
      onClick={() => {
        activeTab === "Sets" && clickHandler(content)
        activeTab === "Types" && clickHandler(faqsHelper.getFaqsByType(content.name))
        activeTab === "Tags" && clickHandler(faqsHelper.getFaqsByTag(content.id))
      }}
      style={{
        background: activeOption.name === content.name ? "lightgray" : "dark",
        height: "auto",
        border: "1px black"
      }}
    >
        {content.name}
        <br></br>
        <Badge
          color={activeOption.name === content.name ? "secondary" : "dark"}
          class="float-right"
        >
          {content.faqs && content.faqs.length}
          {content.total && content.total}
        </Badge>{" "}
      {type !== "Tags" && (
        <small>{content.desc}</small>
      )}
    </div>
  );
}
